// ! Media Queries ! //
// *** Text *** //
@media screen and (max-width: 600px) {
   html {
     font-size: calc(10.24px + 6 * ((100vw - 520px) / 680));
   }
   #guideContent li {
     font-size: calc(10.24px + 6 * ((100vw - 520px) / 680));
   }
   h5 {
     font-size: calc(12.8px + 6 * ((100vw - 520px) / 680));
   }
   h4 {
     font-size: calc(16px + 6 * ((100vw - 520px) / 680));
   }
   h3 {
     font-size: calc(20px + 6 * ((100vw - 520px) / 680));
   }
   h2 {
     font-size: calc(25px + 6 * ((100vw - 520px) / 680));
   }
   h1 {
     font-size: calc(31.25px + 6 * ((100vw - 520px) / 680));
   }
   .guideContent #talents #talentChart {
      width: 80%;
   }
}

// *** Formatting *** //
@media screen and (max-width: 1100px) {
   .main .contentFrame {
      width: 95%;
      #guideContent {
         .guideLeft {
            width: 90%;
         }
         .guideRight {
            display: none;
         }
      }
   }
   .main .navbar {
      width: 95%;
      .headerLeft {
         display: none;
      }
      .headerRight {
         width: 100%;
         .discordHeader {
            display: none;
         }
      }
   }
   .main .siteNav .navBox {
      -webkit-text-stroke-width: 1px;
   }
   // !Home Querries! //
   #homeContent {
      .siteNav {
         .guideNavBoxes {
            display: flex;
            flex-direction: column;
            .navBox {
               margin: 2% 0;
            }
         }
      }
   }
   // !Guide Querries! //
   #guideContent {
      #talents #talentChart {
         width: 90%;
         .talentChartRow {
            flex-direction: column;
            .talentsRight {
               width: 100%;
               display: flex;
               place-content: center;
               margin-top: 2%;
            }
         }
         .talentChartRow li {
            font-size: calc(10.24px + 6 * ((100vw - 520px) / 680));
          }
      }
   }
}

